--
-- Table structure for table `comments`
--

DROP TABLE IF EXISTS `comments`;
CREATE TABLE IF NOT EXISTS `comments` (
  `id` int NOT NULL AUTO_INCREMENT,
  `username` varchar(40) CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NOT NULL,
  `realname` varchar(80) CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NOT NULL,
  `email_auther` varchar(60) CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NOT NULL,
  `state` int NOT NULL,
  `body` text CHARACTER SET utf8mb4 COLLATE utf8mb4_persian_ci NOT NULL,
  `fk_id` int NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_persian_ci;

